mapper.select:
SELECT PET_FILE_NUMBER, PET_FILE_NAME, PET_FILE_UPLOAD_PATH, PET_FILE_UUID, PET_NUMBER
FROM TBL_PET_FILE
WHERE PET_NUMBER = #{petNumber}

mapper.update:
UPDATE TBL_PET_FILE
SET PET_FILE_NAME = #{petFileName}, PET_FILE_UPLOAD_PATH = #{petFileUploadPath}, PET_FILE_UUID = #{petFileUuid}
WHERE PET_NUMBER = #{petNumber}
