APPLY_FILE_UUID, APPLY_FILE_UPLOAD_PATH, APPLY_NUMBER, USER_NUMBER FROM TBL_SITTER_APPLY_LICENSE_FILE
WHERE APPLY_NUMBER = #{applyNumber}

SELECT APPLY_FILE_NUMBER, APPLY_FILE_TITLE, APPLY_FILE_NAME, APPLY_FILE_UUID, APPLY_FILE_UPLOAD_PATH, APPLY_NUMBER, USER_NUMBER
FROM TBL_SITTER_APPLY_LICENSE_FILE
