mapper.update:
UPDATE TBL_USER
SET USER_PASSWORD = #{userPassword}, USER_EMAIL = #{userEmail}, USER_BIRTH = #{userBirth}, USER_PHONE_NUMBER = #{userPhoneNumber}, USER_ADDRESS = #{userAddress}, USER_ZONE_CODE = #{userZoneCode}, USER_ADDRESS_DETAIL = #{userAddressDetail}, USER_ADDRESS_TIP = #{userAddressTip}
WHERE USER_NUMBER = #{userNumber}

mapper.select:
SELECT USER_ID
FROM TBL_USER
WHERE USER_NAME = #{userName} AND USER_PHONE_NUMBER = #{userPhoneNumber}

SELECT COUNT(*)
FROM TBL_USER
WHERE USER_ID = #{userId} AND QUESTION_NUMBER = #{questionNumberInput} AND FIND_PASSWORD_ANSWER = #{answer}

SELECT USER_PASSWORD
FROM TBL_USER
WHERE USER_ID = #{userId}
